import java.util.Scanner;public class DopEx {    /* Дополнительно       Для введённого пользователем с клавиатуры натурального числа посчитайте сумму всех его цифр (заранее       не известно сколько цифр будет в числе).       Например:       Ввод = 12345       Вывод = 1+2+3+4+5 = 15 */    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Введите число: ");        int num = sc.nextInt();        String numStr = Integer.toString(num);        System.out.println("Сумма всех элементов числа равна: " + summaAllElement(numStr));    }    private static Integer summaAllElement(String numStr) {        int res = 0;        for (int i = 0; i < numStr.length(); i++) {            char numChar = numStr.charAt(i);            int elem = Character.getNumericValue(numChar);            res = res + elem;        }        return res;    }}