import java.util.Scanner;public class Home_Work_2 {    /* Реализуйте программу, которая попросит пользователя ввести год и напечатать этот год    isLeap (високосный) или нет. */    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Введите год: ");        int year = sc.nextInt();        isLeap(year);    }    private static void isLeap(int year) {        if ((year % 4 == 0 && year % 100 != 0) || (year % 400 == 0)) {            System.out.println("Этот год " + year + " высокосный ");        } else {            System.out.println("Этот год " + year + " не высокосный ");        }    }}