import java.util.Scanner;public class Home_Work_3 {    /* Реализуйте программу, которая попросит пользователя ввести три целых числа (используйте сканер)    и напечатает максимум из трех чисел. */    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Введите первое число: ");        int num1 = sc.nextInt();        System.out.print("Введите второе число: ");        int num2 = sc.nextInt();        System.out.print("Введите третее число: ");        int num3 = sc.nextInt();        System.out.println("Максимальное значение равно " + maxValue(num1, num2, num3));    }    public static Integer maxValue(int num1, int num2, int num3) {        int result = 0;        if (num1 > num2 && num1 > num3) {            result = num1;        } else if (num2 > num3) {            result = num2;        } else if (num2 < num3) {            result = num3;        }        return result;    }}